package com.deloitte.java.day3.stream;

public class Employee {
	String name;
	int id;
	float salary;

	public Employee(int id, String name, float salary) {
		this.name = name;
		this.id = id;
		this.salary = salary;
	}

	public double getSalary() {
		// TODO Auto-generated method stub
		return salary;
	}
}
